// List of popular stocks for StockTitan scraping
const stockSymbols = [
  'AAPL',
  'MSFT', 
  'GOOGL',
  'AMZN',
  'TSLA',
  'META',
  'NVDA'
];

return stockSymbols.map(symbol => {
  return {
    json: {
      symbol: symbol,
      url: `https://www.stocktitan.net/news/${symbol}/`
    }
  };
});

// Process StockTitan news data
const symbol = $json.symbol || 'UNKNOWN';
const dates = $json.dates || [];
const headlines = $json.headlines || [];
const links = $json.links || [];
const summaries = $json.summaries || [];

console.log(`Processing ${symbol}:`);
console.log(`- Headlines found: ${headlines.length}`);
console.log(`- Links found: ${links.length}`);
console.log(`- Dates found: ${dates.length}`);

// Filter out navigation/menu items and get real news
const realNews = headlines.filter(headline => 
  headline && 
  headline.trim().length > 20 && 
  !headline.toLowerCase().includes('login') &&
  !headline.toLowerCase().includes('sign up') &&
  !headline.toLowerCase().includes('news feed') &&
  !headline.toLowerCase().includes('stock titan')
);

console.log(`- Real news items: ${realNews.length}`);

const results = [];
for (let i = 0; i < Math.min(realNews.length, 5); i++) {
  const headline = realNews[i].trim();

  // Find corresponding link
  let newsLink = '';
  if (links[i] && links[i].trim()) {
    newsLink = links[i].startsWith('http') ? links[i] : `https://www.stocktitan.net${links[i]}`;
  }

  // Find corresponding date
  let publishDate = new Date().toISOString();
  if (dates[i] && dates[i].includes('/')) {
    try {
      publishDate = new Date(dates[i]).toISOString();
    } catch (e) {
      publishDate = new Date().toISOString();
    }
  }

  // Find corresponding summary
  let summary = '';
  if (summaries[i] && summaries[i].trim().length > 20) {
    summary = summaries[i].trim().substring(0, 300);
  }

  results.push({
    symbol: symbol,
    title: headline,
    link: newsLink,
    summary: summary,
    publishedAt: publishDate,
    source: 'stocktitan',
    extracted_at: new Date().toISOString()
  });
}

console.log(`Final results for ${symbol}: ${results.length} items`);
return results.map(item => ({ json: item }));
